import React, { useState, useEffect } from 'react';
import { Box, Typography, TextField, Button, Paper, Grid } from '@mui/material';
import { ArrowBack, ArrowForward, PlayArrow, Pause } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const HashingPage = () => {
  const navigate = useNavigate();
  const [array, setArray] = useState<number[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [targetSum, setTargetSum] = useState<number>(0);
  const [currentStep, setCurrentStep] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [result, setResult] = useState<[number, number] | null>(null);
  const [hashMap, setHashMap] = useState<Map<number, number>>(new Map());
  const [highlightedIndices, setHighlightedIndices] = useState<number[]>([]);

  const handleArrayInput = (value: string) => {
    const numbers = value.split(',').map(num => parseInt(num.trim()));
    if (numbers.every(num => !isNaN(num))) {
      setArray(numbers);
      setCurrentStep(0);
      setResult(null);
      setHashMap(new Map());
      setHighlightedIndices([]);
    }
  };

  const handleTargetSumChange = (value: string) => {
    const sum = parseInt(value);
    if (!isNaN(sum)) {
      setTargetSum(sum);
      setCurrentStep(0);
      setResult(null);
      setHashMap(new Map());
      setHighlightedIndices([]);
    }
  };

  const findPair = () => {
    const map = new Map<number, number>();
    for (let i = 0; i < array.length; i++) {
      const complement = targetSum - array[i];
      if (map.has(complement)) {
        setResult([map.get(complement)!, i]);
        return;
      }
      map.set(array[i], i);
    }
    setResult(null);
  };

  const visualizeStep = (step: number) => {
    if (array.length === 0 || step >= array.length) {
      setHighlightedIndices([]);
      return;
    }

    const currentNum = array[step];
    const complement = targetSum - currentNum;
    const newHashMap = new Map(hashMap);
    newHashMap.set(currentNum, step);
    setHashMap(newHashMap);

    if (hashMap.has(complement)) {
      setHighlightedIndices([hashMap.get(complement)!, step]);
      setResult([hashMap.get(complement)!, step]);
    } else {
      setHighlightedIndices([step]);
    }
  };

  useEffect(() => {
    if (isPlaying) {
      const timer = setInterval(() => {
        setCurrentStep(prev => {
          if (prev >= array.length - 1) {
            setIsPlaying(false);
            return prev;
          }
          return prev + 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [isPlaying, array.length]);

  useEffect(() => {
    visualizeStep(currentStep);
  }, [currentStep, array]);

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Hashing Algorithm
      </Typography>
      
      <Paper sx={{ p: 2, mb: 3 }}>
        <Typography variant="body1" paragraph>
          The Hashing technique is used to find pairs of numbers in an array that sum up to a target value.
          It uses a hash map to store previously seen numbers and their indices, allowing for O(1) lookups.
        </Typography>
      </Paper>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Enter array (comma-separated numbers)"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onBlur={(e) => handleArrayInput(e.target.value)}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Target Sum"
            type="number"
            value={targetSum}
            onChange={(e) => handleTargetSumChange(e.target.value)}
            margin="normal"
          />
        </Grid>

        <Grid item xs={12}>
          <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
            {array.map((num, index) => (
              <Box
                key={index}
                sx={{
                  width: 60,
                  height: 60,
                  border: '2px solid #1976d2',
                  borderRadius: 1,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  mx: 0.5,
                  backgroundColor: highlightedIndices.includes(index) ? '#bbdefb' : 'white',
                  transition: 'background-color 0.3s',
                }}
              >
                <Typography variant="h6">{num}</Typography>
              </Box>
            ))}
          </Box>
        </Grid>

        <Grid item xs={12}>
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 2 }}>
            <Button
              variant="contained"
              startIcon={<ArrowBack />}
              onClick={() => setCurrentStep(prev => Math.max(0, prev - 1))}
              disabled={currentStep === 0}
            >
              Previous
            </Button>
            <Button
              variant="contained"
              onClick={() => setIsPlaying(!isPlaying)}
              startIcon={isPlaying ? <Pause /> : <PlayArrow />}
            >
              {isPlaying ? 'Pause' : 'Play'}
            </Button>
            <Button
              variant="contained"
              endIcon={<ArrowForward />}
              onClick={() => setCurrentStep(prev => Math.min(array.length - 1, prev + 1))}
              disabled={currentStep === array.length - 1}
            >
              Next
            </Button>
          </Box>
        </Grid>

        <Grid item xs={12}>
          <Paper sx={{ p: 2, bgcolor: '#f5f5f5' }}>
            <Typography variant="h6" gutterBottom>
              Hash Map Contents:
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {Array.from(hashMap.entries()).map(([key, value]) => (
                <Paper key={key} sx={{ p: 1, bgcolor: 'white' }}>
                  {key} â†’ {value}
                </Paper>
              ))}
            </Box>
            {result && (
              <Typography variant="h6" sx={{ mt: 2 }}>
                Found pair at indices {result[0]} and {result[1]}
              </Typography>
            )}
            <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
              Step {currentStep + 1} of {array.length}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={12}>
          <Paper sx={{ p: 2, bgcolor: '#f5f5f5' }}>
            <Typography variant="h6" gutterBottom>
              Implementation
            </Typography>
            <pre style={{ margin: 0, whiteSpace: 'pre-wrap' }}>
{`function findPairWithSum(arr: number[], target: number): [number, number] | null {
  const map = new Map<number, number>();
  
  for (let i = 0; i < arr.length; i++) {
    const complement = target - arr[i];
    
    if (map.has(complement)) {
      return [map.get(complement)!, i];
    }
    
    map.set(arr[i], i);
  }
  
  return null;
}`}
            </pre>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default HashingPage;
