"use client"

import type React from "react"
import { useState } from "react"
import { ArrowRight, ArrowLeft, Search, Target, Wind } from "lucide-react"

interface Algorithm {
  name: string
  description: string
  timeComplexity: string
  spaceComplexity: string
  pythonCode: string
}

const algorithms: Algorithm[] = [
  {
    name: "Two Pointer Sum",
    description: "Find pairs of numbers that sum to a target value using two pointers.",
    timeComplexity: "O(n)",
    spaceComplexity: "O(1)",
    pythonCode: `def two_sum_sorted(nums, target):
    left, right = 0, len(nums) - 1
    while left < right:
        current_sum = nums[left] + nums[right]
        if current_sum == target:
            return [left, right]
        elif current_sum < target:
            left += 1
        else:
            right -= 1
    return []`
  },
  {
    name: "Three Pointer Sum",
    description: "Find triplets that sum to a target value using three pointers.",
    timeComplexity: "O(n²)",
    spaceComplexity: "O(1)",
    pythonCode: `def three_sum(nums, target):
    nums.sort()
    result = []
    for i in range(len(nums) - 2):
        if i > 0 and nums[i] == nums[i-1]:
            continue
        left, right = i + 1, len(nums) - 1
        while left < right:
            current_sum = nums[i] + nums[left] + nums[right]
            if current_sum == target:
                result.append([nums[i], nums[left], nums[right]])
                while left < right and nums[left] == nums[left+1]:
                    left += 1
                while left < right and nums[right] == nums[right-1]:
                    right -= 1
                left += 1
                right -= 1
            elif current_sum < target:
                left += 1
            else:
                right -= 1
    return result`
  },
  {
    name: "Sliding Window",
    description: "Find maximum/minimum sum of subarray of fixed size using sliding window technique.",
    timeComplexity: "O(n)",
    spaceComplexity: "O(1)",
    pythonCode: `def max_sum_subarray(nums, k):
    if not nums or k > len(nums):
        return 0
    
    window_sum = sum(nums[:k])
    max_sum = window_sum
    
    for i in range(len(nums) - k):
        window_sum = window_sum - nums[i] + nums[i + k]
        max_sum = max(max_sum, window_sum)
    
    return max_sum`
  }
]

interface ArrayElement {
  value: number
  isHighlighted: boolean
  isPointer?: boolean
  pointerLabel?: string
}

function ArrayAlgorithmsPage() {
  const [array, setArray] = useState<ArrayElement[]>([])
  const [inputValue, setInputValue] = useState<string>("")
  const [targetValue, setTargetValue] = useState<string>("")
  const [windowSize, setWindowSize] = useState<string>("3")
  const [operationHistory, setOperationHistory] = useState<string[]>([])
  const [isAnimating, setIsAnimating] = useState(false)
  const [showFullPythonCode, setShowFullPythonCode] = useState(false)
  const [showCodeMap, setShowCodeMap] = useState<Record<string, boolean>>({})
  const [currentAlgorithm, setCurrentAlgorithm] = useState<string>("two-pointer")

  const toggleCodeVisibility = (algorithmName: string) => {
    setShowCodeMap(prev => ({
      ...prev,
      [algorithmName]: !prev[algorithmName]
    }))
  }

  const addNumber = () => {
    if (!inputValue || isNaN(Number(inputValue))) {
      setOperationHistory(prev => [...prev, "Error: Please enter a valid number!"])
      return
    }

    const newElement = { value: Number(inputValue), isHighlighted: false }
    setArray(prev => [...prev, newElement])
    setOperationHistory(prev => [...prev, `Added ${inputValue} to array`])
    setInputValue("")
  }

  const clearArray = () => {
    setArray([])
    setOperationHistory(prev => [...prev, "Array cleared"])
  }

  const visualizeTwoPointerSum = () => {
    if (array.length < 2) {
      setOperationHistory(prev => [...prev, "Error: Array needs at least 2 elements!"])
      return
    }
    if (!targetValue || isNaN(Number(targetValue))) {
      setOperationHistory(prev => [...prev, "Error: Please enter a valid target value!"])
      return
    }

    setIsAnimating(true)
    const target = Number(targetValue)
    const sortedArray = [...array].sort((a, b) => a.value - b.value)
    
    let left = 0
    let right = sortedArray.length - 1

    const animate = () => {
      if (left >= right) {
        setIsAnimating(false)
        return
      }

      const currentSum = sortedArray[left].value + sortedArray[right].value
      
      setArray(prev => prev.map((el, idx) => ({
        ...el,
        isHighlighted: idx === left || idx === right,
        isPointer: idx === left || idx === right,
        pointerLabel: idx === left ? "L" : idx === right ? "R" : undefined
      })))

      setOperationHistory(prev => [...prev, 
        `Current sum: ${sortedArray[left].value} + ${sortedArray[right].value} = ${currentSum}`
      ])

      if (currentSum === target) {
        setOperationHistory(prev => [...prev, "Found pair!"])
        setTimeout(() => {
          setIsAnimating(false)
          setArray(prev => prev.map(el => ({ ...el, isHighlighted: false, isPointer: false })))
        }, 1000)
        return
      }

      if (currentSum < target) {
        left++
      } else {
        right--
      }

      setTimeout(animate, 1000)
    }

    animate()
  }

  const visualizeSlidingWindow = () => {
    if (array.length < Number(windowSize)) {
      setOperationHistory(prev => [...prev, "Error: Array length must be greater than window size!"])
      return
    }

    setIsAnimating(true)
    const k = Number(windowSize)
    let windowSum = 0
    let maxSum = -Infinity
    let start = 0

    const animate = () => {
      if (start + k > array.length) {
        setIsAnimating(false)
        return
      }

      setArray(prev => prev.map((el, idx) => ({
        ...el,
        isHighlighted: idx >= start && idx < start + k,
        isPointer: idx === start || idx === start + k - 1,
        pointerLabel: idx === start ? "S" : idx === start + k - 1 ? "E" : undefined
      })))

      windowSum = array.slice(start, start + k).reduce((sum, el) => sum + el.value, 0)
      maxSum = Math.max(maxSum, windowSum)

      setOperationHistory(prev => [...prev, 
        `Window [${start}, ${start + k - 1}]: Sum = ${windowSum}, Max Sum = ${maxSum}`
      ])

      start++
      setTimeout(animate, 1000)
    }

    animate()
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-white to-slate-100">
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg">
              <Target className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              Array Algorithms Visualizer
            </h1>
          </div>
          <p className="mt-2 text-gray-600">Visualize array algorithms step by step</p>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {/* Algorithm Information */}
        <div className="mb-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {algorithms.map((algorithm) => (
            <div
              key={algorithm.name}
              className="bg-white rounded-lg shadow-md p-4 border border-gray-200"
            >
              <h3 className="text-lg font-semibold text-gray-900 mb-2 flex justify-between items-center">
                {algorithm.name}
                <button 
                  onClick={() => toggleCodeVisibility(algorithm.name)}
                  className="text-blue-500 hover:text-blue-700 text-sm"
                >
                  {showCodeMap[algorithm.name] ? "Hide Code" : "Show Code"}
                </button>
              </h3>
              <p className="text-gray-600 mb-2">{algorithm.description}</p>
              <div className="text-sm text-gray-500">
                <div>Time Complexity: {algorithm.timeComplexity}</div>
                <div>Space Complexity: {algorithm.spaceComplexity}</div>
              </div>
              {showCodeMap[algorithm.name] && (
                <div className="mt-4 bg-gray-100 p-3 rounded-md overflow-x-auto">
                  <pre><code className="lang-python text-sm">{algorithm.pythonCode}</code></pre>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Array Visualization */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">Array Visualization</h2>
            
            <div className="mb-4">
              <label htmlFor="input-value" className="block text-sm font-medium text-gray-700 mb-2">
                Add Number to Array
              </label>
              <div className="flex gap-2">
                <input
                  id="input-value"
                  type="number"
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  className="flex-1 px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter a number"
                />
                <button
                  onClick={addNumber}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >
                  Add
                </button>
                <button
                  onClick={clearArray}
                  className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
                >
                  Clear
                </button>
              </div>
            </div>

            <div className="mb-4">
              <label htmlFor="target-value" className="block text-sm font-medium text-gray-700 mb-2">
                Target Value
              </label>
              <input
                id="target-value"
                type="number"
                value={targetValue}
                onChange={(e) => setTargetValue(e.target.value)}
                className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500"
                placeholder="Enter target value"
              />
            </div>

            <div className="mb-4">
              <label htmlFor="window-size" className="block text-sm font-medium text-gray-700 mb-2">
                Window Size
              </label>
              <input
                id="window-size"
                type="number"
                value={windowSize}
                onChange={(e) => setWindowSize(e.target.value)}
                className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500"
                min="1"
              />
            </div>

            <div className="flex flex-wrap gap-2 mb-6">
              <button
                onClick={visualizeTwoPointerSum}
                disabled={isAnimating}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
              >
                <Target className="w-5 h-5 inline mr-1" />
                Two Pointer Sum
              </button>
              <button
                onClick={visualizeSlidingWindow}
                disabled={isAnimating}
                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
              >
                <Wind className="w-5 h-5 inline mr-1" />
                Sliding Window
              </button>
            </div>

            {/* Array Display */}
            <div className="border-2 border-gray-300 rounded-lg p-4 min-h-[200px] flex flex-wrap gap-2 items-center">
              {array.map((element, index) => (
                <div
                  key={index}
                  className={`relative w-12 h-12 rounded-lg flex-shrink-0 flex items-center justify-center text-white font-bold transition-all duration-300 ${
                    element.isHighlighted
                      ? "bg-green-500 transform scale-105"
                      : "bg-blue-500"
                  }`}
                >
                  {element.value}
                  {element.isPointer && (
                    <div className="absolute -top-6 left-1/2 transform -translate-x-1/2 text-black font-bold">
                      {element.pointerLabel}
                    </div>
                  )}
                </div>
              ))}
              {array.length === 0 && (
                <div className="text-gray-400 text-center my-auto w-full">Array is empty</div>
              )}
            </div>
          </div>

          {/* Operation History */}
          <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">Operation History</h2>
            <div className="h-[500px] overflow-y-auto border rounded-lg p-4">
              {operationHistory.map((operation, index) => (
                <div
                  key={index}
                  className={`p-2 mb-2 rounded-lg ${
                    operation.startsWith("Error")
                      ? "bg-red-100 text-red-700"
                      : "bg-gray-100 text-gray-700"
                  }`}
                >
                  {operation}
                </div>
              ))}
            </div>
          </div>
        </div>
      </main>
    </div>
  )
}

export default ArrayAlgorithmsPage
